import React, { useState, useContext } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import GeneralContext from "./GeneralContext"; // ✅ Import Context
import "./BuyActionWindow.css";

const BuyActionWindow = ({ uid }) => {
  const [stockQuantity, setStockQuantity] = useState(1);
  const [stockPrice, setStockPrice] = useState(0.0);
  const { closeBuyWindow } = useContext(GeneralContext); // ✅ Use context correctly

  const handleBuyClick = () => {
    axios
      .post("http://localhost:3000/newOrder", {
        name: uid,
        qty: stockQuantity,
        price: stockPrice,
        mode: "BUY",
      })
      .then((response) => {
        console.log("Order placed successfully:", response.data);
      })
      .catch((err) => {
        console.error("Error placing order:", err);
      });

    closeBuyWindow(); // ✅ Correct way to close the buy window
  };

  const handleSellClick = () => {
    axios
      .post("http://localhost:3000/sell", {
        name: uid,
        qty: stockQuantity,
        price: stockPrice,
        mode: "SELL",
      })
      .then((response) => {
        console.log("Order sold successfully:", response.data);
      })
      .catch((err) => {
        console.error("Error placing order:", err);
      });

    closeBuyWindow(); // ✅ Correct way to close the buy window
  };


  const handleCancelClick = () => {
    closeBuyWindow(); // ✅ Correct way to close
  };

  return (
    <div className="container" id="buy-window" draggable="true">
      <div className="regular-order">
        <div className="inputs">
          <fieldset>
            <legend>Qty.</legend>
            <input
              type="number"
              name="qty"
              id="qty"
              onChange={(e) => setStockQuantity(e.target.value)}
              value={stockQuantity}
            />
          </fieldset>
          <fieldset>
            <legend>Price</legend>
            <input
              type="number"
              name="price"
              id="price"
              step="0.05"
              onChange={(e) => setStockPrice(e.target.value)}
              value={stockPrice}
            />
          </fieldset>
        </div>
      </div>

      <div className="buttons">
        <span>Margin required ₹140.65</span>
        <div>
          <Link className="btn btn-blue" onClick={handleBuyClick}>
            Buy
          </Link>
          <Link className="btn btn-grey" onClick={handleCancelClick}>
            Cancel
          </Link>
        </div>
      </div>
    </div>
  );
};

export default BuyActionWindow;
