import React from "react";
import { useState } from "react";
import { Link } from "react-router-dom";

const Menu = () => {

  const [selectedMenu,setSelectedMenu]=useState(0);
  const [isProfileDropdownOpen,setIsProfileDropdownOpen]=useState(false);

   const menuClass="menu";
   const activeMenuClass="menu selected";

  const handleMenuClick=(index)=>
  {
      setSelectedMenu(index);
      console.log(selectedMenu);
  }

  
  const handleProfileOpen=()=>{
       setIsProfileDropdownOpen(!isProfileDropdownOpen);
  }

  return (
    <div className="menu-container">
      <img src="logo.png" style={{ width: "50px" }} />
      <div className="menus">
        <ul>
          <li onClick={()=>handleMenuClick(0)}>
            <Link style={{textDecoration:"none"}} to="/"><p className={selectedMenu===0?menuClass:activeMenuClass}>Dashboard</p></Link>
          </li>
          <li onClick={()=>handleMenuClick(1)}>
          <Link style={{textDecoration:"none"}} to="/orders"><p className={selectedMenu===1?menuClass:activeMenuClass}>Orders</p></Link>
          </li>
          <li onClick={()=>handleMenuClick(2)}>
          <Link style={{textDecoration:"none"}} to="/holdings"><p className={selectedMenu===0?menuClass:activeMenuClass} >Holdings</p></Link>
          </li>
          <li onClick={()=>handleMenuClick(3)}>
          <Link style={{textDecoration:"none"}} to="/positions"><p className={selectedMenu===0?menuClass:activeMenuClass}>Positions</p></Link>
          </li>
          <li onClick={()=>handleMenuClick(4)}>
          <Link style={{textDecoration:"none"}} to="/funds"><p>Funds</p></Link>
          </li>
          <li onClick={()=>handleMenuClick(5)}>
          <Link style={{textDecoration:"none"}} to="/apps"><p>Apps</p></Link>
          </li>
        </ul>
        <hr />
        <div className="profile" >
          <div className="avatar">ZU</div>
          <p className="username">USERID</p>
        </div>
      </div>
    </div>
  );
};

export default Menu;
